import axios from "axios";
import { useEffect, useState } from "react";
import { Table, Button } from "react-bootstrap";
import { useParams } from "react-router";
import Navi from "../components/Navi";

function EveryBoardPage() {
  const { name } = useParams();
  const [index, setIndex] = useState(1);
  const [loading, setLoading] = useState(true);
  const [firstLoading, setFirstLoading] = useState(true);
  const [posts, setPosts] = useState([]);
  const numOfPosts = 20;
  const [scrollTop, setScrollTop] = useState(0);
  const universityname = sessionStorage.getItem("universityname");

  const onScroll = () => {
    console.log("scrolling");
    setScrollTop(document.documentElement.scrollTop);
  };

  const getPosts = async () => {
    const futurePosts = (
      await axios.get(
        `/getposts/${universityname}/${name}/${index}/${numOfPosts}`
      )
    ).data;
    setPosts((currentPosts) => [...currentPosts, ...futurePosts]);
    setIndex((current) => current + 1);
    setLoading(false);
    setFirstLoading(false);
  };
  useEffect(() => {
    window.removeEventListener("scroll", onScroll);
    window.addEventListener("scroll", onScroll, { passive: true });
    if (
      window.innerHeight + document.documentElement.scrollTop ===
      document.scrollingElement.scrollHeight
    ) {
      console.log("update");
      setLoading(true);
      getPosts();
    }
    return () => window.removeEventListener("scroll", onScroll);
  }, [scrollTop]);
  return (
    <div>
      <Navi />
      <div
        style={{
          width: "80%",
          border: "solid",
          borderWidth: "0px 0px 3px 0px",
          borderColor: "black",
          padding: "30px",
          float: "right",
        }}
      >
        <Button href="/newpost">게시글 작성</Button>
      </div>
      {firstLoading ? (
        <div>loading...</div>
      ) : (
        <div>
          <Table>
            <tbody>
              {posts.map((post) => (
                <tr
                  id={post.id}
                  onClick={() => {
                    document.location.href = `/post/${post.id}`;
                  }}
                  style={{ height: "50px" }}
                >
                  <td>{post.title}</td>
                  {/* <td>{post.username}</td> */}
                </tr>
              ))}
            </tbody>
          </Table>
          {loading ? <div>loading</div> : null}
        </div>
      )}
    </div>
  );
}

export default EveryBoardPage;
